mapper.select:
SELECT orders.ORDER_NO, orders.repast_Address, orders.repast_Time, orders.BUYER_ID, orders.price, orders.order_status, dishes.dishes_Id FROM TBL_ORDERS orders JOIN ( SELECT orders.ORDER_NO, group_concat(dishes.DISH_ID) AS dishes_Id FROM TBL_ORDERS orders JOIN TBL_REL_ODR_DISH dishes ON orders.ORDER_NO = dishes.ORDER_NO GROUP BY orders.ORDER_NO ) dishes ON orders.ORDER_NO = dishes.ORDER_NO WHERE 1 = 1
and orders.seller_Id = #{sellerId}
and orders.order_Status = #{orderStatus}
and orders.repast_Mode = #{repastMode}

SELECT dishes.DISH_ID, dishes.DISH_COUNT FROM TBL_REL_ODR_DISH dishes WHERE 1 = 1
and dishes.ORDER_NO = #{orderNo}
and dishes.DISH_ID in
#{dishId}

SELECT * FROM TBL_ORDERS
WHERE 1=1


	and ORDER_NO = #{map.orderNo}


	and seller_Id = #{map.sellerId}


	and order_Status = #{map.order.orderStatus}



	and repast_Mode = #{map.order.repastMode}



ORDER BY #{map.pager.orderBy} #{map.pager.asc} LIMIT
#{map.pager.start}, #{map.pager.end}

SELECT count(*) FROM TBL_ORDERS
WHERE 1=1

	and ORDER_NO = #{orderNo}


	and SELLER_ID = #{sellerId}


	and BUYER_ID = #{buyerId}


	and ORDER_STATUS = #{orderStatus}

SELECT * FROM TBL_ORDERS
WHERE 1=1

	and ORDER_NO = #{orderNo}


	and SELLER_ID = #{sellerId}


	and BUYER_ID = #{buyerId}

 and ORDER_STATUS = #{orderStatus} 


ORDER BY #{orderBy} #{asc}

	LIMIT #{start}, #{pageSize}

mapper.update:
update TBL_ORDERS
set order_status = #{orderStatus}

where ORDER_NO =
#{orderNo}
